pragma solidity 0.8.17;

import "../vendor/@openzeppelin/contracts@4.8.2/security/ReentrancyGuard.sol";
import "../access/AccessControlRegistryAdminnedWithManager.sol";
import "./DataFeedServer.sol";
import "./interfaces/IApi3ServerV1OevExtension.sol";
import "../vendor/@openzeppelin/contracts@4.8.2/utils/Address.sol";
import "../vendor/@openzeppelin/contracts@4.8.2/utils/cryptography/ECDSA.sol";
import "./interfaces/IApi3ServerV1.sol";
import "./interfaces/IApi3ServerV1OevExtensionOevBidPayer.sol";

/// @title Api3ServerV1 extension for OEV support
/// @notice Api3ServerV1 contract supports base data feeds and OEV
/// functionality. This contract implements the updated OEV design, and thus
/// supersedes the OEV-related portion of Api3ServerV1. As before, the users
/// are intended to read API3 data feeds through a standardized proxy, which
/// abstracts this change away.
contract Api3ServerV1OevExtension is
    ReentrancyGuard,
    AccessControlRegistryAdminnedWithManager,
    DataFeedServer,
    IApi3ServerV1OevExtension
{
    using ECDSA for bytes32;

    struct LastPaidBid {
        address updater;
        uint32 signedDataTimestampCutoff;
    }

    /// @notice Withdrawer role description
    string public constant override WITHDRAWER_ROLE_DESCRIPTION = "Withdrawer";

    /// @notice Auctioneer role description
    string public constant override AUCTIONEER_ROLE_DESCRIPTION = "Auctioneer";

    /// @notice Withdrawer role
    bytes32 public immutable override withdrawerRole;

    /// @notice Auctioneer role
    bytes32 public immutable override auctioneerRole;

    /// @notice Api3ServerV1 contract address
    address public immutable override api3ServerV1;

    /// @notice Returns the parameters of the last paid bid for the dApp with
    /// ID
    mapping(uint256 => LastPaidBid) public override dappIdToLastPaidBid;

    bytes32 private constant OEV_BID_PAYMENT_CALLBACK_SUCCESS =
        keccak256("Api3ServerV1OevExtensionOevBidPayer.onOevBidPayment");

    /// @param accessControlRegistry_ AccessControlRegistry contract address
    /// @param adminRoleDescription_ Admin role description
    /// @param manager_ Manager address
    /// @param api3ServerV1_ Api3ServerV1 address
    constructor(
        address accessControlRegistry_,
        string memory adminRoleDescription_,
        address manager_,
        address api3ServerV1_
    )
        AccessControlRegistryAdminnedWithManager(
            accessControlRegistry_,
            adminRoleDescription_,
            manager_
        )
    {
        require(api3ServerV1_ != address(0), "Api3ServerV1 address zero");
        api3ServerV1 = api3ServerV1_;
        withdrawerRole = _deriveRole(
            _deriveAdminRole(manager_),
            WITHDRAWER_ROLE_DESCRIPTION
        );
        auctioneerRole = _deriveRole(
            _deriveAdminRole(manager_),
            AUCTIONEER_ROLE_DESCRIPTION
        );
    }

    /// @dev Used to receive the bid amount in the OEV bid payment callback
    receive() external payable {}

    /// @notice Called by the contract manager or a withdrawer to withdraw the
    /// accumulated OEV auction proceeds
    /// @dev This function has a reentrancy guard to prevent it from being
    /// called in an OEV bid payment callback
    /// @param recipient Recipient address
    /// @param amount Amount
    function withdraw(
        address recipient,
        uint256 amount
    ) external override nonReentrant {
        require(recipient != address(0), "Recipient address zero");
        require(amount != 0, "Amount zero");
        require(
            msg.sender == manager ||
                IAccessControlRegistry(accessControlRegistry).hasRole(
                    withdrawerRole,
                    msg.sender
                ),
            "Sender cannot withdraw"
        );
        (bool success, ) = recipient.call{value: amount}("");
        require(success, "Withdrawal reverted");
        emit Withdrew(recipient, amount, msg.sender);
    }

    /// @notice An OEV auction bid specifies a dApp ID, a signed data timestamp
    /// cut-off, a bid amount and an updater account. To award the winning bid,
    /// an auctioneer signs a message that includes the hash of these
    /// parameters and publishes it. Then, the updater account calls this
    /// function to pay the bid amount and claim the privilege to execute
    /// updates for the dApp with ID using the signed data whose timestamps are
    /// limited by the cut-off. At least the bid amount must be sent to this
    /// contract with empty calldata in the `onOevBidPayment` callback, which
    /// will be checked upon succesful return.
    /// As a result of the reentrancy guard, nesting OEV bid payments is not
    /// allowed.
    /// @param dappId dApp ID
    /// @param bidAmount Bid amount
    /// @param signedDataTimestampCutoff Signed data timestamp cut-off
    /// @param signature Signature provided by an auctioneer
    /// @param data Data that will be passed through the callback
    function payOevBid(
        uint256 dappId,
        uint256 bidAmount,
        uint32 signedDataTimestampCutoff,
        bytes calldata signature,
        bytes calldata data
    ) external override nonReentrant {
        require(dappId != 0, "dApp ID zero");
        require(signedDataTimestampCutoff != 0, "Cut-off zero");
        // It is intended for the auction periods to be in the order of a
        // minute. To prevent erroneously large cut-off timestamps from causing
        // an irreversible state change to the contract, we do not allow
        // cut-off values that are too far in the future.
        require(
            signedDataTimestampCutoff < block.timestamp + 1 hours,
            "Cut-off too far in the future"
        );
        address auctioneer = (
            keccak256(
                abi.encodePacked(
                    block.chainid,
                    dappId,
                    msg.sender,
                    bidAmount,
                    signedDataTimestampCutoff
                )
            ).toEthSignedMessageHash()
        ).recover(signature);
        require(
            IAccessControlRegistry(accessControlRegistry).hasRole(
                auctioneerRole,
                auctioneer
            ),
            "Signature mismatch"
        );
        require(
            dappIdToLastPaidBid[dappId].signedDataTimestampCutoff <
                signedDataTimestampCutoff,
            "Cut-off not more recent"
        );
        dappIdToLastPaidBid[dappId] = LastPaidBid({
            updater: msg.sender,
            signedDataTimestampCutoff: signedDataTimestampCutoff
        });
        uint256 balanceBefore = address(this).balance;
        require(
            IApi3ServerV1OevExtensionOevBidPayer(msg.sender).onOevBidPayment(
                bidAmount,
                data
            ) == OEV_BID_PAYMENT_CALLBACK_SUCCESS,
            "OEV bid payment callback failed"
        );
        require(
            address(this).balance - balanceBefore >= bidAmount,
            "OEV bid payment amount short"
        );
        emit PaidOevBid(
            dappId,
            msg.sender,
            bidAmount,
            signedDataTimestampCutoff,
            auctioneer
        );
    }

    /// @notice Called by the current updater of the dApp with ID to update the
    /// OEV data feed specific to the dApp
    /// @param dappId dApp ID
    /// @param signedData Signed data (see `_updateDappOevDataFeed()` for
    /// details)
    /// @return baseDataFeedId Base data feed ID
    /// @return updatedValue Updated value
    /// @return updatedTimestamp Updated timestamp
    function updateDappOevDataFeed(
        uint256 dappId,
        bytes[] calldata signedData
    )
        external
        override
        returns (
            bytes32 baseDataFeedId,
            int224 updatedValue,
            uint32 updatedTimestamp
        )
    {
        LastPaidBid storage lastPaidBid = dappIdToLastPaidBid[dappId];
        require(
            msg.sender == lastPaidBid.updater,
            "Sender not last bid updater"
        );
        (
            baseDataFeedId,
            updatedValue,
            updatedTimestamp
        ) = _updateDappOevDataFeed(
            dappId,
            lastPaidBid.signedDataTimestampCutoff,
            signedData
        );
        emit UpdatedDappOevDataFeed(
            dappId,
            msg.sender,
            baseDataFeedId,
            updatedValue,
            updatedTimestamp
        );
    }

    /// @notice Called by the zero address to simulate an OEV data feed update
    /// @dev The intended flow is for a searcher to do a static multicall to
    /// this function and `simulateExternalCall()` to check if the current
    /// signed data lets them extract OEV. If so, the searcher stores this data
    /// and places a bid on OevAuctionHouse. If they win the auction, they pay
    /// the bid and use the stored signed data with `updateDappOevDataFeed()`
    /// to extract OEV.
    /// @param dappId dApp ID
    /// @param signedData Signed data (see `_updateDappOevDataFeed()` for
    /// details)
    /// @return baseDataFeedId Base data feed ID
    /// @return updatedValue Updated value
    /// @return updatedTimestamp Updated timestamp
    function simulateDappOevDataFeedUpdate(
        uint256 dappId,
        bytes[] calldata signedData
    )
        external
        override
        returns (
            bytes32 baseDataFeedId,
            int224 updatedValue,
            uint32 updatedTimestamp
        )
    {
        require(msg.sender == address(0), "Sender address not zero");
        (
            baseDataFeedId,
            updatedValue,
            updatedTimestamp
        ) = _updateDappOevDataFeed(dappId, type(uint256).max, signedData);
    }

    /// @notice Called by the zero address to simulate an external call
    /// @dev The most basic usage of this is in a static multicall that calls
    /// `simulateDappOevDataFeedUpdate()` multiple times to update the relevant
    /// feeds, followed by an external call to the liquidator contract of the
    /// searcher, which is built to return the revenue from the liquidation.
    /// The returned value would then be used to determine the bid amount.
    /// @param target Target address of the external call
    /// @param data Calldata of the external call
    /// @return Returndata of the external call
    function simulateExternalCall(
        address target,
        bytes calldata data
    ) external override returns (bytes memory) {
        require(msg.sender == address(0), "Sender address not zero");
        return Address.functionCall(target, data);
    }

    /// @notice Value of the OEV data feed specific to the dApp, intended for
    /// informational purposes. The dApps are strongly recommended to use the
    /// standardized proxies to read data feeds.
    /// @param dappId dApp ID
    /// @param dataFeedId Data feed ID
    /// @return value Data feed value
    /// @return timestamp Data feed timestamp
    function oevDataFeed(
        uint256 dappId,
        bytes32 dataFeedId
    ) external view override returns (int224 value, uint32 timestamp) {
        DataFeed storage dataFeed = _dataFeeds[
            keccak256(abi.encodePacked(dappId, dataFeedId))
        ];
        (value, timestamp) = (dataFeed.value, dataFeed.timestamp);
    }

    /// @notice Updates OEV data feed specific to the dApp with the signed data
    /// @dev This function replicates the guarantees of base feed updates,
    /// which makes OEV updates exactly as secure as base feed updates. The
    /// main difference between base feed updates and OEV feed updates is that
    /// the signature for OEV updates use the hash of the respective template
    /// ID (while the base feed updates use the template ID as is).
    /// @param dappId dApp ID
    /// @param signedDataTimestampCutoff Signed data timestamp cut-off
    /// @param signedData Signed data that is a bytes array. Each item in the
    /// array is the Airnode address, template ID, data feed timestamp, data
    /// feed value and signature belonging to each Beacon. Similar to base feed
    /// updates, OEV feed updates allow individual Beacon updates to be omitted
    /// (in this case by leaving the signature empty) in case signed data for
    /// some of the Beacons is not available.
    /// @return baseDataFeedId Base data feed ID
    /// @return updatedValue Updated value
    /// @return updatedTimestamp Updated timestamp
    function _updateDappOevDataFeed(
        uint256 dappId,
        uint256 signedDataTimestampCutoff,
        bytes[] calldata signedData
    )
        private
        returns (
            bytes32 baseDataFeedId,
            int224 updatedValue,
            uint32 updatedTimestamp
        )
    {
        uint256 beaconCount = signedData.length;
        require(beaconCount > 0, "Signed data empty");
        if (beaconCount == 1) {
            (
                address airnode,
                bytes32 templateId,
                uint256 timestamp,
                bytes memory data,
                bytes memory signature
            ) = abi.decode(
                    signedData[0],
                    (address, bytes32, uint256, bytes, bytes)
                );
            baseDataFeedId = deriveBeaconId(airnode, templateId);
            // Each base feed has an OEV equivalent specific to each dApp. The
            // ID of these OEV feeds are simply the dApp ID and the base data
            // feed ID hashed together, independent from if the base feed is a
            // Beacon or Beacon set.
            bytes32 oevBeaconId = keccak256(
                abi.encodePacked(dappId, baseDataFeedId)
            );
            // The signature cannot be omitted for a single Beacon
            require(
                (
                    keccak256(
                        abi.encodePacked(
                            keccak256(abi.encodePacked(templateId)),
                            timestamp,
                            data
                        )
                    ).toEthSignedMessageHash()
                ).recover(signature) == airnode,
                "Signature mismatch"
            );
            require(
                timestamp <= signedDataTimestampCutoff,
                "Timestamp exceeds cut-off"
            );
            require(
                timestamp > _dataFeeds[oevBeaconId].timestamp,
                "Does not update timestamp"
            );
            updatedValue = decodeFulfillmentData(data);
            updatedTimestamp = uint32(timestamp);
            // We do not need to check if the base feed has a larger timestamp,
            // as the proxy will prefer the base feed if it has a larger
            // timestamp anyway
            _dataFeeds[oevBeaconId] = DataFeed({
                value: updatedValue,
                timestamp: updatedTimestamp
            });
        } else {
            bytes32[] memory baseBeaconIds = new bytes32[](beaconCount);
            bytes32[] memory oevBeaconIds = new bytes32[](beaconCount);
            for (uint256 ind = 0; ind < beaconCount; ind++) {
                (
                    address airnode,
                    bytes32 templateId,
                    uint256 timestamp,
                    bytes memory data,
                    bytes memory signature
                ) = abi.decode(
                        signedData[ind],
                        (address, bytes32, uint256, bytes, bytes)
                    );
                baseBeaconIds[ind] = deriveBeaconId(airnode, templateId);
                // We also store individual Beacons of an OEV feed to make sure
                // that their timestamps are not reduced by OEV updates
                oevBeaconIds[ind] = keccak256(
                    abi.encodePacked(dappId, baseBeaconIds[ind])
                );
                if (signature.length != 0) {
                    require(
                        (
                            keccak256(
                                abi.encodePacked(
                                    keccak256(abi.encodePacked(templateId)),
                                    timestamp,
                                    data
                                )
                            ).toEthSignedMessageHash()
                        ).recover(signature) == airnode,
                        "Signature mismatch"
                    );
                    require(
                        timestamp <= signedDataTimestampCutoff,
                        "Timestamp exceeds cut-off"
                    );
                    require(
                        timestamp > _dataFeeds[oevBeaconIds[ind]].timestamp,
                        "Does not update timestamp"
                    );
                    _dataFeeds[oevBeaconIds[ind]] = DataFeed({
                        value: decodeFulfillmentData(data),
                        timestamp: uint32(timestamp)
                    });
                }
                // Without the following bit, an OEV update would effectively
                // be able to reduce the timestamps of individual Beacons of a
                // Beacon set.
                (
                    int224 baseBeaconValue,
                    uint32 baseBeaconTimestamp
                ) = IApi3ServerV1(api3ServerV1).dataFeeds(baseBeaconIds[ind]);
                if (
                    baseBeaconTimestamp >
                    _dataFeeds[oevBeaconIds[ind]].timestamp
                ) {
                    // Carrying over base feed values to OEV feeds is fine
                    // because they are secured by identical guarantees
                    _dataFeeds[oevBeaconIds[ind]] = DataFeed({
                        value: baseBeaconValue,
                        timestamp: baseBeaconTimestamp
                    });
                }
            }
            baseDataFeedId = deriveBeaconSetId(baseBeaconIds);
            (updatedValue, updatedTimestamp) = aggregateBeacons(oevBeaconIds);
            bytes32 oevBeaconSetId = keccak256(
                abi.encodePacked(dappId, baseDataFeedId)
            );
            DataFeed storage oevBeaconSet = _dataFeeds[oevBeaconSetId];
            if (oevBeaconSet.timestamp == updatedTimestamp) {
                require(
                    oevBeaconSet.value != updatedValue,
                    "Does not update Beacon set"
                );
            }
            _dataFeeds[oevBeaconSetId] = DataFeed({
                value: updatedValue,
                timestamp: updatedTimestamp
            });
        }
    }
}
","keccak256":"0x16ee98b2cd72844234c7721510dc4c2c526b3eb7a6aff7bd0c6fe8143b975e01","license":"MIT"},"contracts/api3-server-v1/DataFeedServer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../utils/ExtendedSelfMulticall.sol";
import "./aggregation/Median.sol";
import "./interfaces/IDataFeedServer.sol";
import "../vendor/@openzeppelin/contracts@4.8.2/utils/cryptography/ECDSA.sol";

/// @title Contract that serves Beacons and Beacon sets
/// @notice A Beacon is a live data feed addressed by an ID, which is derived
/// from an Airnode address and a template ID. This is suitable where the more
/// recent data point is always more favorable, e.g., in the context of an
/// asset price data feed. Beacons can also be seen as one-Airnode data feeds
/// that can be used individually or combined to build Beacon sets.
contract DataFeedServer is ExtendedSelfMulticall, Median, IDataFeedServer {
    using ECDSA for bytes32;

    // Airnodes serve their fulfillment data along with timestamps. This
    // contract casts the reported data to `int224` and the timestamp to
    // `uint32`, which works until year 2106.
    struct DataFeed {
        int224 value;
        uint32 timestamp;
    }

    /// @notice Data feed with ID
    mapping(bytes32 => DataFeed) internal _dataFeeds;

    /// @dev Reverts if the timestamp is from more than 1 hour in the future
    modifier onlyValidTimestamp(uint256 timestamp) virtual {
        unchecked {
            require(
                timestamp < block.timestamp + 1 hours,
                "Timestamp not valid"
            );
        }
        _;
    }

    /// @notice Updates the Beacon set using the current values of its Beacons
    /// @dev As an oddity, this function still works if some of the IDs in
    /// `beaconIds` belong to Beacon sets rather than Beacons. This can be used
    /// to implement hierarchical Beacon sets.
    /// @param beaconIds Beacon IDs
    /// @return beaconSetId Beacon set ID
    function updateBeaconSetWithBeacons(
        bytes32[] memory beaconIds
    ) public override returns (bytes32 beaconSetId) {
        (int224 updatedValue, uint32 updatedTimestamp) = aggregateBeacons(
            beaconIds
        );
        beaconSetId = deriveBeaconSetId(beaconIds);
        DataFeed storage beaconSet = _dataFeeds[beaconSetId];
        if (beaconSet.timestamp == updatedTimestamp) {
            require(
                beaconSet.value != updatedValue,
                "Does not update Beacon set"
            );
        }
        _dataFeeds[beaconSetId] = DataFeed({
            value: updatedValue,
            timestamp: updatedTimestamp
        });
        emit UpdatedBeaconSetWithBeacons(
            beaconSetId,
            updatedValue,
            updatedTimestamp
        );
    }

    /// @notice Reads the data feed with ID
    /// @param dataFeedId Data feed ID
    /// @return value Data feed value
    /// @return timestamp Data feed timestamp
    function _readDataFeedWithId(
        bytes32 dataFeedId
    ) internal view returns (int224 value, uint32 timestamp) {
        DataFeed storage dataFeed = _dataFeeds[dataFeedId];
        (value, timestamp) = (dataFeed.value, dataFeed.timestamp);
        require(timestamp > 0, "Data feed not initialized");
    }

    /// @notice Derives the Beacon ID from the Airnode address and template ID
    /// @param airnode Airnode address
    /// @param templateId Template ID
    /// @return beaconId Beacon ID
    function deriveBeaconId(
        address airnode,
        bytes32 templateId
    ) internal pure returns (bytes32 beaconId) {
        beaconId = keccak256(abi.encodePacked(airnode, templateId));
    }

    /// @notice Derives the Beacon set ID from the Beacon IDs
    /// @dev Notice that `abi.encode()` is used over `abi.encodePacked()`
    /// @param beaconIds Beacon IDs
    /// @return beaconSetId Beacon set ID
    function deriveBeaconSetId(
        bytes32[] memory beaconIds
    ) internal pure returns (bytes32 beaconSetId) {
        beaconSetId = keccak256(abi.encode(beaconIds));
    }

    /// @notice Called privately to process the Beacon update
    /// @param beaconId Beacon ID
    /// @param timestamp Timestamp used in the signature
    /// @param data Fulfillment data (an `int256` encoded in contract ABI)
    /// @return updatedBeaconValue Updated Beacon value
    function processBeaconUpdate(
        bytes32 beaconId,
        uint256 timestamp,
        bytes calldata data
    )
        internal
        onlyValidTimestamp(timestamp)
        returns (int224 updatedBeaconValue)
    {
        updatedBeaconValue = decodeFulfillmentData(data);
        require(
            timestamp > _dataFeeds[beaconId].timestamp,
            "Does not update timestamp"
        );
        _dataFeeds[beaconId] = DataFeed({
            value: updatedBeaconValue,
            timestamp: uint32(timestamp)
        });
    }

    /// @notice Called privately to decode the fulfillment data
    /// @param data Fulfillment data (an `int256` encoded in contract ABI)
    /// @return decodedData Decoded fulfillment data
    function decodeFulfillmentData(
        bytes memory data
    ) internal pure returns (int224) {
        require(data.length == 32, "Data length not correct");
        int256 decodedData = abi.decode(data, (int256));
        require(
            decodedData >= type(int224).min && decodedData <= type(int224).max,
            "Value typecasting error"
        );
        return int224(decodedData);
    }

    /// @notice Called privately to aggregate the Beacons and return the result
    /// @param beaconIds Beacon IDs
    /// @return value Aggregation value
    /// @return timestamp Aggregation timestamp
    function aggregateBeacons(
        bytes32[] memory beaconIds
    ) internal view returns (int224 value, uint32 timestamp) {
        uint256 beaconCount = beaconIds.length;
        require(beaconCount > 1, "Specified less than two Beacons");
        int256[] memory values = new int256[](beaconCount);
        int256[] memory timestamps = new int256[](beaconCount);
        for (uint256 ind = 0; ind < beaconCount; ) {
            DataFeed storage dataFeed = _dataFeeds[beaconIds[ind]];
            values[ind] = dataFeed.value;
            timestamps[ind] = int256(uint256(dataFeed.timestamp));
            unchecked {
                ind++;
            }
        }
        value = int224(median(values));
        timestamp = uint32(uint256(median(timestamps)));
    }
}
","keccak256":"0xff3172da608fcaa59f17557ff06f8355a90d8cf87c3b3a91b8405ec122a064e3","license":"MIT"},"contracts/api3-server-v1/aggregation/Median.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Sort.sol";
import "./QuickSelect.sol";

/// @title Contract to be inherited by contracts that will calculate the median
/// of an array
/// @notice The operation will be in-place, i.e., the array provided as the
/// argument will be modified.
contract Median is Sort, Quickselect {
    /// @notice Returns the median of the array
    /// @dev Uses an unrolled sorting implementation for shorter arrays and
    /// quickselect for longer arrays for gas cost efficiency
    /// @param array Array whose median is to be calculated
    /// @return Median of the array
    function median(int256[] memory array) internal pure returns (int256) {
        uint256 arrayLength = array.length;
        if (arrayLength <= MAX_SORT_LENGTH) {
            sort(array);
            if (arrayLength % 2 == 1) {
                return array[arrayLength / 2];
            } else {
                assert(arrayLength != 0);
                unchecked {
                    return
                        average(
                            array[arrayLength / 2 - 1],
                            array[arrayLength / 2]
                        );
                }
            }
        } else {
            if (arrayLength % 2 == 1) {
                return array[quickselectK(array, arrayLength / 2)];
            } else {
                uint256 mid1;
                uint256 mid2;
                unchecked {
                    (mid1, mid2) = quickselectKPlusOne(
                        array,
                        arrayLength / 2 - 1
                    );
                }
                return average(array[mid1], array[mid2]);
            }
        }
    }

    /// @notice Averages two signed integers without overflowing
    /// @param x Integer x
    /// @param y Integer y
    /// @return Average of integers x and y
    function average(int256 x, int256 y) private pure returns (int256) {
        unchecked {
            int256 averageRoundedDownToNegativeInfinity = (x >> 1) +
                (y >> 1) +
                (x & y & 1);
            // If the average rounded down to negative infinity is negative
            // (i.e., its 256th sign bit is set), and one of (x, y) is even and
            // the other one is odd (i.e., the 1st bit of their xor is set),
            // add 1 to round the average down to zero instead.
            // We will typecast the signed integer to unsigned to logical-shift
            // int256(uint256(signedInt)) >> 255 ~= signedInt >>> 255
            return
                averageRoundedDownToNegativeInfinity +
                (int256(
                    (uint256(averageRoundedDownToNegativeInfinity) >> 255)
                ) & (x ^ y));
        }
    }
}
","keccak256":"0x84912d10ad5fa4848f2dfbbac40431ccdeb5250c263718b5fbdfeceeb0946524","license":"MIT"},"contracts/api3-server-v1/aggregation/QuickSelect.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/// @title Contract to be inherited by contracts that will calculate the index
/// of the k-th and optionally (k+1)-th largest elements in the array
/// @notice Uses quickselect, which operates in-place, i.e., the array provided
/// as the argument will be modified.
contract Quickselect {
    /// @notice Returns the index of the k-th largest element in the array
    /// @param array Array in which k-th largest element will be searched
    /// @param k K
    /// @return indK Index of the k-th largest element
    function quickselectK(
        int256[] memory array,
        uint256 k
    ) internal pure returns (uint256 indK) {
        uint256 arrayLength = array.length;
        assert(arrayLength > 0);
        unchecked {
            (indK, ) = quickselect(array, 0, arrayLength - 1, k, false);
        }
    }

    /// @notice Returns the index of the k-th and (k+1)-th largest elements in
    /// the array
    /// @param array Array in which k-th and (k+1)-th largest elements will be
    /// searched
    /// @param k K
    /// @return indK Index of the k-th largest element
    /// @return indKPlusOne Index of the (k+1)-th largest element
    function quickselectKPlusOne(
        int256[] memory array,
        uint256 k
    ) internal pure returns (uint256 indK, uint256 indKPlusOne) {
        uint256 arrayLength = array.length;
        assert(arrayLength > 1);
        unchecked {
            (indK, indKPlusOne) = quickselect(
                array,
                0,
                arrayLength - 1,
                k,
                true
            );
        }
    }

    /// @notice Returns the index of the k-th largest element in the specified
    /// section of the (potentially unsorted) array
    /// @param array Array in which K will be searched for
    /// @param lo Starting index of the section of the array that K will be
    /// searched in
    /// @param hi Last index of the section of the array that K will be
    /// searched in
    /// @param k K
    /// @param selectKPlusOne If the index of the (k+1)-th largest element is
    /// to be returned
    /// @return indK Index of the k-th largest element
    /// @return indKPlusOne Index of the (k+1)-th largest element (only set if
    /// `selectKPlusOne` is `true`)
    function quickselect(
        int256[] memory array,
        uint256 lo,
        uint256 hi,
        uint256 k,
        bool selectKPlusOne
    ) private pure returns (uint256 indK, uint256 indKPlusOne) {
        if (lo == hi) {
            return (k, 0);
        }
        uint256 indPivot = partition(array, lo, hi);
        if (k < indPivot) {
            unchecked {
                (indK, ) = quickselect(array, lo, indPivot - 1, k, false);
            }
        } else if (k > indPivot) {
            unchecked {
                (indK, ) = quickselect(array, indPivot + 1, hi, k, false);
            }
        } else {
            indK = indPivot;
        }
        // Since Quickselect ends in the array being partitioned around the
        // k-th largest element, we can continue searching towards right for
        // the (k+1)-th largest element, which is useful in calculating the
        // median of an array with even length
        if (selectKPlusOne) {
            unchecked {
                indKPlusOne = indK + 1;
            }
            uint256 i;
            unchecked {
                i = indKPlusOne + 1;
            }
            uint256 arrayLength = array.length;
            for (; i < arrayLength; ) {
                if (array[i] < array[indKPlusOne]) {
                    indKPlusOne = i;
                }
                unchecked {
                    i++;
                }
            }
        }
    }

    /// @notice Partitions the array into two around a pivot
    /// @param array Array that will be partitioned
    /// @param lo Starting index of the section of the array that will be
    /// partitioned
    /// @param hi Last index of the section of the array that will be
    /// partitioned
    /// @return pivotInd Pivot index
    function partition(
        int256[] memory array,
        uint256 lo,
        uint256 hi
    ) private pure returns (uint256 pivotInd) {
        if (lo == hi) {
            return lo;
        }
        int256 pivot = array[lo];
        uint256 i = lo;
        unchecked {
            pivotInd = hi + 1;
        }
        while (true) {
            do {
                unchecked {
                    i++;
                }
            } while (i < array.length && array[i] < pivot);
            do {
                unchecked {
                    pivotInd--;
                }
            } while (array[pivotInd] > pivot);
            if (i >= pivotInd) {
                (array[lo], array[pivotInd]) = (array[pivotInd], array[lo]);
                return pivotInd;
            }
            (array[i], array[pivotInd]) = (array[pivotInd], array[i]);
        }
    }
}
","keccak256":"0x7f458dd165fed89866c5fe626e3df3c9bf6884498ec1233f4083615084521d6e","license":"MIT"},"contracts/api3-server-v1/aggregation/Sort.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/// @title Contract to be inherited by contracts that will sort an array using
/// an unrolled implementation
/// @notice The operation will be in-place, i.e., the array provided as the
/// argument will be modified.
contract Sort {
    uint256 internal constant MAX_SORT_LENGTH = 9;

    /// @notice Sorts the array
    /// @param array Array to be sorted
    function sort(int256[] memory array) internal pure {
        uint256 arrayLength = array.length;
        require(arrayLength <= MAX_SORT_LENGTH, "Array too long to sort");
        // Do a binary search
        if (arrayLength < 6) {
            // Possible lengths: 1, 2, 3, 4, 5
            if (arrayLength < 4) {
                // Possible lengths: 1, 2, 3
                if (arrayLength == 3) {
                    // Length: 3
                    swapIfFirstIsLarger(array, 0, 1);
                    swapIfFirstIsLarger(array, 1, 2);
                    swapIfFirstIsLarger(array, 0, 1);
                } else if (arrayLength == 2) {
                    // Length: 2
                    swapIfFirstIsLarger(array, 0, 1);
                }
                // Do nothing for Length: 1
            } else {
                // Possible lengths: 4, 5
                if (arrayLength == 5) {
                    // Length: 5
                    swapIfFirstIsLarger(array, 1, 2);
                    swapIfFirstIsLarger(array, 3, 4);
                    swapIfFirstIsLarger(array, 1, 3);
                    swapIfFirstIsLarger(array, 0, 2);
                    swapIfFirstIsLarger(array, 2, 4);
                    swapIfFirstIsLarger(array, 0, 3);
                    swapIfFirstIsLarger(array, 0, 1);
                    swapIfFirstIsLarger(array, 2, 3);
                    swapIfFirstIsLarger(array, 1, 2);
                } else {
                    // Length: 4
                    swapIfFirstIsLarger(array, 0, 1);
                    swapIfFirstIsLarger(array, 2, 3);
                    swapIfFirstIsLarger(array, 1, 3);
                    swapIfFirstIsLarger(array, 0, 2);
                    swapIfFirstIsLarger(array, 1, 2);
                }
            }
        } else {
            // Possible lengths: 6, 7, 8, 9
            if (arrayLength < 8) {
                // Possible lengths: 6, 7
                if (arrayLength == 7) {
                    // Length: 7
                    swapIfFirstIsLarger(array, 1, 2);
                    swapIfFirstIsLarger(array, 3, 4);
                    swapIfFirstIsLarger(array, 5, 6);
                    swapIfFirstIsLarger(array, 0, 2);
                    swapIfFirstIsLarger(array, 4, 6);
                    swapIfFirstIsLarger(array, 3, 5);
                    swapIfFirstIsLarger(array, 2, 6);
                    swapIfFirstIsLarger(array, 1, 5);
                    swapIfFirstIsLarger(array, 0, 4);
                    swapIfFirstIsLarger(array, 2, 5);
                    swapIfFirstIsLarger(array, 0, 3);
                    swapIfFirstIsLarger(array, 2, 4);
                    swapIfFirstIsLarger(array, 1, 3);
                    swapIfFirstIsLarger(array, 0, 1);
                    swapIfFirstIsLarger(array, 2, 3);
                    swapIfFirstIsLarger(array, 4, 5);
                } else {
                    // Length: 6
                    swapIfFirstIsLarger(array, 0, 1);
                    swapIfFirstIsLarger(array, 2, 3);
                    swapIfFirstIsLarger(array, 4, 5);
                    swapIfFirstIsLarger(array, 1, 3);
                    swapIfFirstIsLarger(array, 3, 5);
                    swapIfFirstIsLarger(array, 1, 3);
                    swapIfFirstIsLarger(array, 2, 4);
                    swapIfFirstIsLarger(array, 0, 2);
                    swapIfFirstIsLarger(array, 2, 4);
                    swapIfFirstIsLarger(array, 3, 4);
                    swapIfFirstIsLarger(array, 1, 2);
                    swapIfFirstIsLarger(array, 2, 3);
                }
            } else {
                // Possible lengths: 8, 9
                if (arrayLength == 9) {
                    // Length: 9
                    swapIfFirstIsLarger(array, 1, 8);
                    swapIfFirstIsLarger(array, 2, 7);
                    swapIfFirstIsLarger(array, 3, 6);
                    swapIfFirstIsLarger(array, 4, 5);
                    swapIfFirstIsLarger(array, 1, 4);
                    swapIfFirstIsLarger(array, 5, 8);
                    swapIfFirstIsLarger(array, 0, 2);
                    swapIfFirstIsLarger(array, 6, 7);
                    swapIfFirstIsLarger(array, 2, 6);
                    swapIfFirstIsLarger(array, 7, 8);
                    swapIfFirstIsLarger(array, 0, 3);
                    swapIfFirstIsLarger(array, 4, 5);
                    swapIfFirstIsLarger(array, 0, 1);
                    swapIfFirstIsLarger(array, 3, 5);
                    swapIfFirstIsLarger(array, 6, 7);
                    swapIfFirstIsLarger(array, 2, 4);
                    swapIfFirstIsLarger(array, 1, 3);
                    swapIfFirstIsLarger(array, 5, 7);
                    swapIfFirstIsLarger(array, 4, 6);
                    swapIfFirstIsLarger(array, 1, 2);
                    swapIfFirstIsLarger(array, 3, 4);
                    swapIfFirstIsLarger(array, 5, 6);
                    swapIfFirstIsLarger(array, 7, 8);
                    swapIfFirstIsLarger(array, 2, 3);
                    swapIfFirstIsLarger(array, 4, 5);
                } else {
                    // Length: 8
                    swapIfFirstIsLarger(array, 0, 7);
                    swapIfFirstIsLarger(array, 1, 6);
                    swapIfFirstIsLarger(array, 2, 5);
                    swapIfFirstIsLarger(array, 3, 4);
                    swapIfFirstIsLarger(array, 0, 3);
                    swapIfFirstIsLarger(array, 4, 7);
                    swapIfFirstIsLarger(array, 1, 2);
                    swapIfFirstIsLarger(array, 5, 6);
                    swapIfFirstIsLarger(array, 0, 1);
                    swapIfFirstIsLarger(array, 2, 3);
                    swapIfFirstIsLarger(array, 4, 5);
                    swapIfFirstIsLarger(array, 6, 7);
                    swapIfFirstIsLarger(array, 3, 5);
                    swapIfFirstIsLarger(array, 2, 4);
                    swapIfFirstIsLarger(array, 1, 2);
                    swapIfFirstIsLarger(array, 3, 4);
                    swapIfFirstIsLarger(array, 5, 6);
                    swapIfFirstIsLarger(array, 2, 3);
                    swapIfFirstIsLarger(array, 4, 5);
                    swapIfFirstIsLarger(array, 3, 4);
                }
            }
        }
    }

    /// @notice Swaps two elements of an array if the first element is greater
    /// than the second
    /// @param array Array whose elements are to be swapped
    /// @param ind1 Index of the first element
    /// @param ind2 Index of the second element
    function swapIfFirstIsLarger(
        int256[] memory array,
        uint256 ind1,
        uint256 ind2
    ) private pure {
        if (array[ind1] > array[ind2]) {
            (array[ind1], array[ind2]) = (array[ind2], array[ind1]);
        }
    }
}
